<div class="containerx mb-4">
    <div class="columna3">
        <div>
           <img src="https://secure.ibw.com.ni/caja/images/ibw.png" alt="ibw">
       </div>
       <div class="title">	
           <h2>
               ORDEN DE AVERIA
           </h2>
       </div> 
       <div>&nbsp;</div>
    </div>
    <div class="box2">
           <li><b>No. Orden: <span style="color:red"> {{doc.name}}</span></b></li>
           <li><b>Servicio:</b> {{doc.servicio}}</li>
           <li><b>Estado: </b>{{doc.workflow_state}}</li>
    </div>
    <div class="text-center"><b>Fecha:</b> {{  frappe.utils.formatdate(frappe.utils.nowdate(), "dd-MM-yyyy")}}</div>
</div>
<section>
    <div class="container-z mb-1">
        <div><b>INFORMACION DEL CLIENTE</b></div>
        <div class="columna3">
            <li><b>RegNumb: </b>{{doc.customer}}</li>
            <li><b>Telefono: </b>{%if doc.numero_de_telefono %}{{doc.numero_de_telefono}}{%endif%}</li>
            <li><b>Ciudad:  </b>{%if doc.municipio%}{{doc.municipio}}{%else%} {%endif%}</li>
        </div>
        <div><b>Cliente: </b>{{doc.nombre}}</div>
        <div><b>Dirección: </b>{%if doc.address_line1%}{{doc.address_line1}}{%else%} {%endif%}</div>
    </div>
</section>
<section class="mt-1">
    <div class="container-w">
        <div><b>INFORMACION TECNICA</b></div>    
        <div class="columna2">
            <li><b>Abierto Fecha: </b>{{doc.fecha_y_hora}}</li>
            <li><b>Abierto Por: </b>{{doc.owner}}</li>
        </div>  
        <div class="columna2">
            <li><b>Cerrado Fecha: </b>{%if doc.fecha_finalizado%}{{doc.fecha_finalizado}}{%endif%}</li>
            <li><b>Cerrado Por: </b>{% if doc.finalizado_por %}{{doc.finalizado_por}} {%endif%}</li>
        </div>
        <div><b>Grupo Técnico >>></b></div>
        <div><b>Averia Catalogada como:</b>{{doc.issue_type}} </div>
        <div style="height:15mm; width:165mm; text-overflow: '-';"><b>Descripción: </b><small>{%if doc.descripcion%}{{doc.descripcion}}{%endif%}</small></div>
        <div><b>Solución Catalogada como: </b>{%if doc.detalle_solucion%}{{doc.detalle_solucion}}{%endif%}</div>
        <div style="height:15mm; width:125mm;"><b>Solución: </b><small>{%if doc.resolution_details%}{{doc.resolution_details}}{%endif%}</small></div>
        <div style="width:125mm;">
            <b>Equipos: </b>
            <small>
                {%if doc.equipos%}
                    {%for mac in doc.equipos%}
                    {{mac.equipo}},&nbsp;
                    {%endfor%}
                {%endif%}    
            </small>
        </div>
        <div class="box3">
           <li class="mt-1"><b><small>Costo de Visita $:</small> ___________</b></li>
           <li>&nbsp;</li>
           <li><b>_________________________</b></li>
           <li class="text-center"><b><small>Firma del Cliente</small></b></li>
        </div>
    </div>
</section>

